def is_true(x):
    sum = 0
    i=1
    while i < int(x/2)+2:
        if x%i == 0:
            sum += i
        i+=1
    if sum > x:
        return True
    return False
def NotAble(x):
    i=1
    while i < int(x/2)+2:
        if is_true(i) and is_true(x-i):
            return False
    return True
