def perm_cnt(weights):
    l = len(weights)
    return reduce(lambda x,y: x*y, range(1,l+1))  # get l!

def get_ord_of_val_in_perm(weights, value):

    return 100

def nth_perm(weights, order):
    #if type(order)!='int' or type(order)!='long':
    #   return "invalid parameter order"
    #if type(weights)!='str':
    #   return "invalid parameter weights"

    if order > perm_cnt(weights) or order<1:
        return "";

    # generated list of [..., 3!, 2!, 1!]
    facts = map(lambda i: reduce(lambda x,y: x*y, range(1, i+1)), range(1, len(weights)))[::-1]

    s = ''
    q =  n = order
    for i in facts:
        q  = n/i
        n -= q*i  # remainder
        if n==0:  # get biggest value
            q -= 1

        s += weights[q]
        weights = weights[:q]+weights[q+1:]  # weights.remove(q)
        if n==0:
            # s += reduce(lambda x, y: x+y, reversed(weights))
            s += weights[::-1]  # str.reverse()
            break
        elif n==1:
            s += weights
            break
    return s


def perm(weights):
    cnt = perm_cnt(weights)
    # print "perm count " + str(cnt)
    return map(lambda i : nth_perm(weights,i+1), range(cnt))


